import React, { useState } from "react";
import useCollapse from 'react-collapsed';

function ProjectForm({ saveProject, teacherId }) {
  const [projectName, setProjectName] = useState("");
  const [projectDescription, setProjectDescription] = useState("");
  const [projectType, setProjectType] = useState("");
  const [projectDomain, setProjectDomain] = useState("");
  const [cgpaCutoff, setCGPACutoff] = useState("");
  const [prerequisites, setPrerequisites] = useState([]);
  const [newPrerequisite, setNewPrerequisite] = useState("");

  const handleAddPrerequisite = () => {
    if (newPrerequisite.trim() !== "") {
      setPrerequisites([...prerequisites, newPrerequisite]);
      setNewPrerequisite("");
    }
  };

  const handleDeletePrerequisite = (index) => {
    const updatedPrerequisites = [...prerequisites];
    updatedPrerequisites.splice(index, 1);
    setPrerequisites(updatedPrerequisites);
  };

  const handleSaveProject = () => {
    const projectData = {
      teacherId, // Include teacherId in projectData
      projectName,
      projectDescription,
      projectType,
      projectDomain,
      cgpaCutoff,
      prerequisites,
    };
    saveProject(projectData);
  };

  const config = {
    duration: 2000,
    onExpandStart: () => {
      console.log('INFO: onExpandStart triggered.');
    },
    onExpandEnd: () => {
      console.log('INFO: onExpandEnd triggered.');
    },
    onCollapseStart: () => {
      console.log('INFO: onCollapseStart triggered.');
    },
    onCollapseEnd: () => {
      console.log('INFO: onCollapseEnd triggered.');
    }
  };
  const { getCollapseProps, getToggleProps, isExpanded } = useCollapse(config);

  return (
    <div className="collapsible">
      <div className="header" {...getToggleProps()}>
        {isExpanded ? 'Collapse' : 'Expand'}
      </div>
      <div {...getCollapseProps()}>
        <div className="content">
          <h2>Add Project</h2>
          <form>
            {/* Form inputs */}
            {/* Handle form inputs and buttons */}
          </form>
        </div>
      </div>
    </div>
  );
}

export default ProjectForm;
