// teacher_home.js
import React, { useState, useEffect } from "react";
import ProjectForm from "./form";
import { useParams } from "react-router-dom";
import {
  CButton,
  CCollapse,
  CCard,
  CCardBody,
  CCardTitle,
  CCardText,
} from "@coreui/react";

function TeacherHome() {
  const [visible, setVisible] = useState(false);
  const [projects, setProjects] = useState([]);
  const { userId } = useParams();

  useEffect(() => {
    // Fetch projects for the current teacher
    fetch(`http://localhost:8000/projects?teacherId=${userId}`)
      .then((response) => response.json())
      .then((data) => {
        setProjects(data);
      })
      .catch((error) => {
        console.error("Error fetching projects:", error);
      });
  }, [userId]);

  const saveProject = (projectData) => {
    projectData.teacherId = userId;

    fetch("http://localhost:8000/saveProject", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(projectData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Project saved successfully:", data);
        // After saving, fetch updated project list
        fetch(`http://localhost:8000/projects?teacherId=${userId}`)
          .then((response) => response.json())
          .then((data) => {
            setProjects(data);
          })
          .catch((error) => {
            console.error("Error fetching projects:", error);
          });
      })
      .catch((error) => {
        console.error("Error saving project:", error);
      });
  };

  return (
    <>
      <CButton
        className="mb-3"
        onClick={() => setVisible(!visible)}
        aria-expanded={visible}
        aria-controls="collapseWidthExample"
      >
        Add Project
      </CButton>
      <div>
        <CCollapse id="collapseWidthExample" visible={visible}>
          <CCard style={{ width: "70%", left: "14%" }}>
            <CCardBody>
              <ProjectForm
                saveProject={saveProject}
                closeModal={() => setVisible(false)}
                teacherId={userId}
              />
            </CCardBody>
          </CCard>
        </CCollapse>
      </div>
      <hr />
      <div>
        <h3>Current Projects</h3>
        {projects.map((project, index) => (
          <CCard key={index} style={{ width: "18rem" }}>
            <CCardBody>
              <CCardTitle>{project.projectName}</CCardTitle>
              <CCardText>
                Description: {project.projectDescription}<br />
                CGPA Cutoff: {project.cgpaCutoff}
              </CCardText>
              <CButton>View</CButton>
              <CButton>Edit</CButton>
              <CButton>Delete</CButton>
            </CCardBody>
          </CCard>
        ))}
      </div>
    </>
  );
}

export default TeacherHome;
