// client/src/components/teacher/teacher_home.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './';

const TeacherHome = ({ userId }) => {
  const [projects, setProjects] = useState([]);
  const [projectForm, setProjectForm] = useState({
    projectName: '',
    projectDescription: '',
    projectDomain: '',
    showModal: false // Add this line
  });
  

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/api/projects/${userId}`);
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, [userId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProjectForm({ ...projectForm, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8000/api/projects', {
        teacherId: userId,
        ...projectForm
      });
      fetchProjects(); // Refresh projects after adding
      setProjectForm({
        projectName: '',
        projectDescription: '',
        projectDomain: ''
      });
    } catch (error) {
      console.error('Error adding project:', error);
    }
  };
  const handleClick = () => {
    setProjectForm({
      ...projectForm,
      showModal: !projectForm.showModal
    });
  };
  
  
  const handleDelete = async (projectId) => {
    try {
      await axios.delete(`http://localhost:8000/api/projects/${projectId}`);
      fetchProjects(); // Refresh projects after deleting
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Teacher Home</h1>
      <button onClick={handleClick}>Add Project</button>
      <div>
        {projects.map((project) => (
          <div key={project._id}>
            <h3>{project.project_name}</h3>
            <p>{project.project_description}</p>
            <p>{project.project_domain}</p>
            <button onClick={() => handleDelete(project._id)}>Delete</button>
          </div>
        ))}
      </div>
      {projectForm.showModal && (
  <div className="modal">
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="projectName"
        placeholder="Project Name"
        value={projectForm.projectName}
        onChange={handleInputChange}
        required
      />
      <input
        type="text"
        name="projectDescription"
        placeholder="Project Description"
        value={projectForm.projectDescription}
        onChange={handleInputChange}
        required
      />
      <input
        type="text"
        name="projectDomain"
        placeholder="Project Domain"
        value={projectForm.projectDomain}
        onChange={handleInputChange}
        required
      />
      <button type="submit">Save</button>
      <button onClick={() => setProjectForm({ ...projectForm, showModal: false })}>Cancel</button>
    </form>
  </div>
)}

    </div>
  );
};

export default TeacherHome;
