import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Dropdown } from 'primereact/dropdown';
import { TriStateCheckbox } from 'primereact/tristatecheckbox';
import axios from 'axios';

export default function ProjectBank({ userId }) {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [globalFilterValue, setGlobalFilterValue] = useState('');
    const [filters, setFilters] = useState({
        project_name: { value: null },
        project_domain: { value: null },
        teacher_name: { value: null },
        project_type: { value: null },
        department: { value: null },
        cg_eligibility: { value: null }
    });

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/students/projectBank/${userId}`);
                setProjects(response.data);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching projects:', error);
            }
        };

        fetchProjects();
    }, [userId]);

    const onGlobalFilterChange = (e) => {
        setGlobalFilterValue(e.target.value);
    };

    const header = (
        <div className="flex justify-content-end">
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText value={globalFilterValue} onChange={onGlobalFilterChange} placeholder="Keyword Search" />
            </span>
        </div>
    );

    return (
        <div className="card">
            <DataTable value={projects} paginator rows={10} dataKey="project_name" filters={filters}
                header={header} emptyMessage="No projects found." loading={loading} globalFilter={globalFilterValue}>
                <Column field="project_name" header="Project Name" filter filterPlaceholder="Search by name" />
                <Column field="project_description" header="Project Description" />
                <Column field="project_type" header="Project Type" filter filterPlaceholder="Select" />
                <Column field="project_domain" header="Project Domain" filter filterPlaceholder="Search by domain" />
                <Column field="teacher_name" header="Teacher Name" filter filterPlaceholder="Search by name" />
                <Column field="department" header="Department" filter filterPlaceholder="Select" />
                <Column field="pre_requisites" header="Pre-requisites" />
                <Column field="cg_eligibility" header="CG Eligibility" filter filterPlaceholder="Select" />
            </DataTable>
        </div>
    );
}
