import React, { useState } from 'react';
import ProjectForm from './projectForm';

const TeacherHome = () => {
  const [showModal, setShowModal] = useState(false);

  const handleAddProject = (project) => {
    // Send project data to server to save
    // You can use fetch or axios for sending HTTP requests
    // Example: fetch('/api/projects', { method: 'POST', body: JSON.stringify(project) })
    // Handle success and error responses accordingly
    setShowModal(false);
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Teacher Home</h1>
      <button onClick={() => setShowModal(true)}>Add Project</button>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowModal(false)}>
              &times;
            </span>
            <ProjectForm onSubmit={handleAddProject} />
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherHome;
