import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProjectDetailsForm = ({ editingProject, setEditingProject, setShowProjectForm, setProjects, userId }) => {
    const [projectDetails, setProjectDetails] = useState({
        project_name: '',
        project_type: '',
        project_description: '',
        project_domain: '',
        cg_cutoff: '',
        pre_requisites: []
    });

    useEffect(() => {
        if (editingProject) {
            setProjectDetails(editingProject);
        }
    }, [editingProject]);

    const handleChange = (e) => {
        setProjectDetails({ ...projectDetails, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (editingProject) {
                await axios.put(`http://localhost:8000/projects/${editingProject._id}`, projectDetails);
                setProjects(projects.map(project => project._id === editingProject._id ? projectDetails : project));
                setEditingProject(null);
            } else {
                await axios.post(`http://localhost:8000/projects`, { ...projectDetails, teacherId: userId });
                setProjects([...projects, projectDetails]);
            }
            setShowProjectForm(false);
        } catch (error) {
            console.error('Error saving project:', error);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="text" name="project_name" value={projectDetails.project_name} onChange={handleChange} placeholder="Project Name" />
                {/* Add more input fields for other project details */}
                <button type="submit">{editingProject ? 'UPDATE' : 'SAVE'}</button>
                <button onClick={() => setShowProjectForm(false)}>CANCEL</button>
            </form>
        </div>
    );
};

export default ProjectDetailsForm;
