import React, { useState } from 'react';
import {
  CModal,
  CModalHeader,
  CModalTitle,
  CModalBody,
  CModalFooter,
  CButton,
  CFormInput
} from '@coreui/react';

const RequestFormModal = ({ visible, onClose }) => {
  const [formData, setFormData] = useState({
    // Initialize form data state here
  });

  const [showConfirmation, setShowConfirmation] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = () => {
    // Show confirmation message
    setShowConfirmation(true);
  };

  const handleConfirmSendRequest = () => {
    // Handle sending request here
    // Once request is sent, close the modal
    setShowConfirmation(false);
    onClose();
  };

  const handleBack = () => {
    // Hide confirmation message
    setShowConfirmation(false);
  };

  const handleSaveDraft = () => {
    console.log("Save Draft")
  }

  return (
    <CModal
      backdrop="static"
      visible={visible}
      onClose={onClose}
      aria-labelledby="RequestFormModalTitle"
    >
      <CModalHeader closeButton>
        <CModalTitle id="RequestFormModalTitle">Request Form</CModalTitle>
      </CModalHeader>
      <CModalBody>
        {/* Form fields */}
        {/* Example input field */}
        <CFormInput
          type="text"
          name="fieldName"
          value={formData.fieldName}
          onChange={handleInputChange}
          placeholder="Enter field name"
        />
        {/* Add more input fields as needed */}
      </CModalBody>
      <CModalFooter>
        {!showConfirmation ? (
          <React.Fragment>
            <CButton color="secondary" onClick={onClose}>
              Close
            </CButton>
            <CButton color="info" onClick={handleSaveDraft}>
              Save Draft
            </CButton>
            <CButton color="primary" onClick={handleSubmit}>
              Send Request
            </CButton>
          </React.Fragment>
        ) : (
          <React.Fragment>
            <CButton color="secondary" onClick={handleBack}>
              Back
            </CButton>
            <CButton color="success" onClick={handleConfirmSendRequest}>
              Confirm Send Request
            </CButton>
          </React.Fragment>
        )}
      </CModalFooter>
    </CModal>
  );
};

export default RequestFormModal;
