// Frontend Login.js
import React from 'react';
import "./Login.css";

const Login = () => {
  const handleGoogleLogin = (userType) => {
    // Additional validation for student and teacher email formats
    if ((userType === 'teacher' && userType.startsWith('f')) || 
        (userType === 'student' && !userType.startsWith('f'))) {
      window.location.href = 'http://localhost:3000/'; // Redirect to error page
    } else {
      window.location.href = `http://localhost:8000/auth/google?user_type=${userType}`; // Redirect to Google authentication
    }
  }

  return (
    <div className="login-page">
      <h1 style={{ textAlign: "center" }}>Login</h1>
      <div className='form'>
        <button className='login-with-google-btn' onClick={() => handleGoogleLogin('teacher')}>Login as Teacher</button>
        <button className='login-with-google-btn' onClick={() => handleGoogleLogin('student')}>Login as Student</button>
      </div>
    </div>
  );
}

export default Login;
