import React, { useState } from 'react';
import './teacher_home.css';

const TeacherHome = () => {
  const [showModal, setShowModal] = useState(false);
  const [prerequisites, setPrerequisites] = useState([]);

  const handleAddPreRequisite = () => {
    const newPreRequisite = document.getElementById('new-pre-req').value;
    if (newPreRequisite.trim()) {
      setPrerequisites((prev) => [...prev, { value: newPreRequisite, showCross: false }]);
      document.getElementById('new-pre-req').value = '';
    }
  };

  const handleDeletePreRequisite = (index) => {
    const updatedPrerequisites = [...prerequisites];
    updatedPrerequisites.splice(index, 1);
    setPrerequisites(updatedPrerequisites);
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Teacher Home</h1>
      <button onClick={() => setShowModal(true)}>ADD PROJECT</button>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowModal(false)}>
              &times;
            </span>
            <h2>Add Project</h2>
            <form>
              <label htmlFor="project-name">Project Name:</label>
              <input type="text" id="project-name" name="project-name" />
              <br />

              <label htmlFor="project-description">Project Description:</label>
              <textarea id="project-description" name="project-description"></textarea>
              <br />

              <label>Project Type:</label>
              <div>
                <input type="radio" id="lop" name="project-type" value="LOP" />
                <label htmlFor="lop">LOP</label>
              </div>
              <div>
                <input type="radio" id="dop" name="project-type" value="DOP" />
                <label htmlFor="dop">DOP</label>
              </div>
              <div>
                <input type="radio" id="sop" name="project-type" value="SOP" />
                <label htmlFor="sop">SOP</label>
              </div>
              <br />

              <label>Project Domain:</label>
              <select id="project-domain" name="project-domain">
                <option value="CS">CS</option>
                <option value="ECE">ECE</option>
                <option value="ENI">ENI</option>
                <option value="EEE">EEE</option>
              </select>
              <br />

              <label>Pre-requisites:</label>
              <div className="pre-req-container">
                <input
                  type="text"
                  id="new-pre-req"
                  name="new-pre-req"
                />
                <button onClick={handleAddPreRequisite}>Add</button>
              </div>
              <ul>
                {prerequisites.map((preReq, index) => (
                  <li key={index}>
                    {preReq.value}
                    <span
                      className="delete-icon"
                      onClick={() =>
                        setPrerequisites((prev) =>
                          prev.map((p, i) =>
                            i === index ? { ...p, showCross: true } : p
                          )
                        )
                      }
                    >
                      ✕
                    </span>
                    {preReq.showCross && (
                      <span
                        className="cross-icon"
                        onClick={() => handleDeletePreRequisite(index)} > ✕ </span> )
                        } 
                        </li> ))} </ul> </form> </div> </div> )} </div> ); };

export default TeacherHome
