import './App.css';
import Login from './components/Login';
import Error from './components/Error';
import React from 'react';
import Header from './components/Headers';
import StudentHome from './components/student/student_home';
import TeacherHome from './components/teacher/teacher_home';
import ProjectBank from './components/common/project_bank';
import StudentProfile from './components/student/student_profile';
import TeacherProfile from './components/teacher/teacher_profile';
import { Route, Routes, useParams } from 'react-router-dom'; // Remove unnecessary imports

function App() {
  return (
    <>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/StudentHome/:userId" element={<StudentApp />} /> {/* Wrap StudentHome and StudentProfile routes in a separate component */}
        <Route path="/TeacherHome/:userId" element={<TeacherApp />} /> {/* Wrap TeacherHome and TeacherProfile routes in a separate component */}
        <Route path= "/project_bank" element={<ProjectBank />} />
        <Route path="*" element={<Error />} />
      </Routes>
    </>
  );
}

// StudentApp component
function StudentApp() {
  const { userId } = useParams();
  return (
    <>
      <Header userId={userId} /> {/* Pass userId as a prop to Header component */}
      <Routes>
        <Route path="/" element={<StudentHome />} />
        <Route path="/StudentProfile" element={<StudentProfile />} /> {/* You can access userId from props in StudentProfile component */}
      </Routes>
    </>
  );
}

// TeacherApp component
function TeacherApp() {
  const { userId } = useParams();
  return (
    <>
      <Header userId={userId} /> {/* Pass userId as a prop to Header component */}
      <Routes>
        <Route path="/" element={<TeacherHome />} />
        <Route path="/TeacherProfile" element={<TeacherProfile />} /> {/* You can access userId from props in TeacherProfile component */}
      </Routes>
    </>
  );
}

export default App;
