import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectDetails from './ProjectDetails';

const TeacherHome = ({ match }) => {
  const [projects, setProjects] = useState([]);
  const [editingProject, setEditingProject] = useState(null);
  const [newProject, setNewProject] = useState({
    project_name: '',
    project_type: '',
    project_description: '',
    project_domain: '',
    cg_cutoff: '',
    pre_requisites: []
  });

  const userId = match.params.userId;

  useEffect(() => {
    // Fetch projects belonging to the teacher
    axios.get(`http://localhost:8000/projects/${userId}`)
      .then(res => {
        setProjects(res.data);
      })
      .catch(err => console.error(err));
  }, [userId]);

  const handleSaveProject = () => {
    if (editingProject) {
      // Update existing project
      axios.put(`http://localhost:8000/projects/${editingProject._id}`, editingProject)
        .then(res => {
          setEditingProject(null);
          setProjects(prevProjects => prevProjects.map(project => project._id === res.data._id ? res.data : project));
        })
        .catch(err => console.error(err));
    } else {
      // Add new project
      axios.post('http://localhost:8000/projects', { ...newProject, teacherId: userId })
        .then(res => {
          setProjects(prevProjects => [...prevProjects, res.data]);
          setNewProject({
            project_name: '',
            project_type: '',
            project_description: '',
            project_domain: '',
            cg_cutoff: '',
            pre_requisites: []
          });
        })
        .catch(err => console.error(err));
    }
  };

  const handleEditProject = (project) => {
    setEditingProject(project);
    setNewProject(project);
  };

  const handleDeleteProject = (projectId) => {
    axios.delete(`http://localhost:8000/projects/${projectId}`)
      .then(() => {
        setProjects(prevProjects => prevProjects.filter(project => project._id !== projectId));
      })
      .catch(err => console.error(err));
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Teacher Home</h1>
      <button onClick={() => setEditingProject(null)}>Add Project</button>
      {editingProject &&
        <ProjectDetails
          project={newProject}
          setProject={setNewProject}
          onSave={handleSaveProject}
        />
      }
      <div>
        {projects.map(project => (
          <div key={project._id}>
            <h3>{project.project_name}</h3>
            <p>{project.project_description}</p>
            <button onClick={() => handleEditProject(project)}>Edit</button>
            <button onClick={() => handleDeleteProject(project._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeacherHome;
