// EditModal.js
import React, { useState } from "react";
import {
  CButton,
  CModal,
  CModalHeader,
  CModalTitle,
  CModalBody,
  CModalFooter,
} from '@coreui/react';

function EditModal({ projectId, closeModal }) {
  const [visible, setVisible] = useState(true); // Initially set to true to show the modal

  return (
    <CModal
      backdrop="static"
      visible={visible}
      onClose={() => {
        setVisible(false);
        closeModal(); // Call the parent closeModal function to close the modal
      }}
      aria-labelledby="EditModalLabel"
    >
      <CModalHeader closeButton>
        <CModalTitle id="EditModalLabel">Edit Project</CModalTitle>
      </CModalHeader>
      <CModalBody>
        {/* Here you can add your form elements for editing the project */}
        <p>Editing Project: {projectId}</p>
      </CModalBody>
      <CModalFooter>
        <CButton color="secondary" onClick={() => closeModal()}>
          Close
        </CButton>
        <CButton color="primary">Save changes</CButton>
      </CModalFooter>
    </CModal>
  );
}

export default EditModal;
