import React from 'react';
import { useParams } from 'react-router-dom';

// ... (existing imports)

const TeacherProfile = () => {
  const { userId } = useParams();
  const [formData, setFormData] = useState({
    name: "",
    roomNumber: "",
    department: "",
  });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    // Fetch teacher data based on userId and update formData
    // ...

    // Example of updating form data if teacher data is available
    setFormData({
      name: "Hrishikesh Samala",
      roomNumber: "",
      department: "",
    });
  }, [userId]);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = async () => {
    // Save formData to the teachers collection
    // ...

    // Example of updating teacher data in the database
    const updatedTeacherData = {
      userId: userId,
      name: formData.name,
      roomNumber: formData.roomNumber,
      department: formData.department,
    };

    // Update teacher data in the database
    // ...

    setIsEditing(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  return (
    <div>
      <h1>My Profile</h1>
      <p>Name: {formData.name || "Not Available"}</p>
      <p>Room Number: {formData.roomNumber || "Not Available"}</p>
      <p>Department: {formData.department || "Not Available"}</p>

      {isEditing ? (
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
          <br />

          <label>Room Number:</label>
          <input
            type="text"
            name="roomNumber"
            value={formData.roomNumber}
            onChange={handleChange}
          />
          <br />

          <label>Department:</label>
          <input
            type="text"
            name="department"
            value={formData.department}
            onChange={handleChange}
          />
          <br />

          <button onClick={handleSaveClick}>SAVE</button>
        </div>
      ) : (
        <button onClick={handleEditClick}>EDIT</button>
      )}
    </div>
  );
};

export default TeacherProfile;
