import React, { useState, useEffect } from 'react';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Dropdown } from 'primereact/dropdown';
import { TriStateCheckbox } from 'primereact/tristatecheckbox';
import { useParams } from 'react-router-dom';
import "primereact/resources/themes/lara-light-cyan/theme.css";

import axios from 'axios';

export default function ProjectBankPage({ match }) {
    const {userId} = useParams();
    const [projects, setProjects] = useState([]);
    const [globalFilterValue, setGlobalFilterValue] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        async function fetchProjects() {
            try {
                const response = await axios.get(`http://localhost:8000/students/projectBank/${userId}`);
                setProjects(response.data);
                setLoading(false);
            } catch (error) {
                console.error("Error fetching projects:", error);
            }
        }

        fetchProjects();
    }, [userId]);

    const onGlobalFilterChange = (e) => {
      const value = e.target.value;
      let _filters = { ...filters };

      _filters['global'].value = value;

      setFilters(_filters);
      setGlobalFilterValue(value);
    };

    const header = (
        <div className="flex justify-content-end">
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText value={globalFilterValue} onChange={onGlobalFilterChange} placeholder="Keyword Search" />
            </span>
        </div>
    );

    return (
        <div className="card">
            <DataTable value={projects} paginator rows={2} loading={loading}
                globalFilterFields={['project_name', 'project_domain', 'teacher_name']}
                header={header} emptyMessage="No projects found.">
                <Column field="project_name" header="Project Name" filter filterPlaceholder="Search by name" />
                <Column field="project_description" header="Project Description" />
                <Column field="project_type" header="Project Type" filter filterElement={
                    <Dropdown options={['LOP', 'DOP', 'SOP']} showClear placeholder="Select" />
                } />
                <Column field="project_domain" header="Project Domain" />
                <Column field="teacher_name" header="Teacher Name" />
                <Column field="department" header="Department" />
                <Column field="pre_requisites" header="Pre-requisites" />
                <Column field="cg_eligibility" header="CG Eligibility" filter filterElement={
                    <TriStateCheckbox value={null} onChange={(e) => {}} />
                } />
            </DataTable>
        </div>
    );
}
