import React, { useState, useEffect } from "react";
import {
  CButton,
  CModal,
  CModalHeader,
  CModalTitle,
  CModalBody,
  CModalFooter,
  CForm,
  CFormInput,
  CFormTextarea,
  CFormSelect,
  CInputGroup,
  CContainer,
  CRow,
  CCol,
  CBadge,
  CCloseButton
} from '@coreui/react';

function EditModal({ projectId, closeModal }) {
  const [projectData, setProjectData] = useState({
    projectName: "",
    projectDescription: "",
    projectType: "",
    cgpaCutoff: "",
    projectDomain: "",
    prerequisites: [],
    newPrerequisite: ""
  });

  useEffect(() => {
    const fetchProjectData = async () => {
      try {
        const response = await fetch(`http://localhost:8000/projectdata/${projectId}`);
        if (!response.ok) {
          throw new Error("Failed to fetch project data");
        }
        const data = await response.json();
        console.log("Fetched project data:", data); // Log the fetched data
        setProjectData({
          projectName: data.project.project_name,
          projectDescription: data.project.project_description,
          projectType: data.project.project_type,
          cgpaCutoff: data.project.cg_cutoff,
          projectDomain: data.project.project_domain,
          prerequisites: data.project.pre_requisites,
          newPrerequisite: ""
        });
      } catch (error) {
        console.error("Error fetching project data:", error);
      }
    };
  
    fetchProjectData();
  }, []); // Empty dependency array ensures this effect runs only once on component mount

  const handleSaveProject = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:8000/saveProject", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          projectId,
          ...projectData
        }),
      });
      const data = await response.json();
      console.log("Project updated successfully:", data);
      closeModal();
    } catch (error) {
      console.error("Error saving project:", error);
    }
  };

  const handleAddPrerequisite = () => {
    setProjectData(prevState => ({
      ...prevState,
      prerequisites: [...prevState.prerequisites, prevState.newPrerequisite],
      newPrerequisite: ""
    }));
  };

  const handleDeletePrerequisite = (index) => {
    setProjectData(prevState => ({
      ...prevState,
      prerequisites: prevState.prerequisites.filter((_, i) => i !== index)
    }));
  };

  return (
    <CModal
      backdrop="static"
      visible={true}
      onClose={closeModal}
      aria-labelledby="EditModalLabel"
    >
      <CModalHeader closeButton>
        <CModalTitle id="EditModalLabel">Edit Project</CModalTitle>
      </CModalHeader>
      <CModalBody>
        <CForm className="row g-3 needs-validation" noValidate onSubmit={handleSaveProject}>
          <CContainer>
            <CRow>
              <CCol>
                <CFormInput
                  type="text"
                  value={projectData.projectName || ""}
                  onChange={(e) => setProjectData({...projectData, projectName: e.target.value})}
                  required
                />
              </CCol>
              <CCol xs={6}>
                <CFormTextarea
                  value={projectData.projectDescription || ""}
                  onChange={(e) => setProjectData({...projectData, projectDescription: e.target.value})}
                  required
                />
              </CCol>
            </CRow>
            {/* Remaining form fields */}
          </CContainer>
        </CForm>
      </CModalBody>
      <CModalFooter>
        <CButton color="secondary" onClick={closeModal}>
          Close
        </CButton>
        <CButton color="primary" type="submit">
          Save changes
        </CButton>
      </CModalFooter>
    </CModal>
  );
}

export default EditModal;
