// client/src/components/teacher/teacher_home.js
import React, { useState, useEffect } from 'react';
import ProjectModal from './ProjectModal';
import axios from 'axios';

const TeacherHome = ({ userId }) => {
  const [projects, setProjects] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState(null);

  useEffect(() => {
    // Fetch projects for the teacher
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/projects/${userId}`);
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const toggleModal = (project) => {
    setSelectedProject(project);
    setShowModal(!showModal);
  };

  const addProject = async (projectData) => {
    try {
      await axios.post('http://localhost:8000/projects', { ...projectData, teacherId: userId });
      fetchProjects(); // Refresh projects after adding
      setShowModal(false);
    } catch (error) {
      console.error('Error adding project:', error);
    }
  };

  const editProject = async (projectId, projectData) => {
    try {
      await axios.put(`http://localhost:8000/projects/${projectId}`, projectData);
      fetchProjects(); // Refresh projects after editing
      setShowModal(false);
    } catch (error) {
      console.error('Error editing project:', error);
    }
  };

  const deleteProject = async (projectId) => {
    try {
      await axios.delete(`http://localhost:8000/projects/${projectId}`);
      fetchProjects(); // Refresh projects after deleting
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Teacher Home</h1>
      <button onClick={() => toggleModal(null)}>Add Project</button>
      {projects.map((project) => (
        <div key={project._id}>
          <h3>{project.project_name}</h3>
          <p>{project.project_description}</p>
          <p>{project.project_domain}</p>
          <button onClick={() => toggleModal(project)}>Edit</button>
          <button onClick={() => deleteProject(project._id)}>Delete</button>
        </div>
      ))}
      {showModal && (
        <ProjectModal
          show={showModal}
          onClose={() => setShowModal(false)}
          onSave={selectedProject ? editProject : addProject}
          project={selectedProject}
        />
      )}
    </div>
  );
};

export default TeacherHome;
