import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TeacherHome = () => {
  const [projects, setProjects] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editedProjectId, setEditedProjectId] = useState('');
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [cgCutoff, setCgCutoff] = useState('');
  const [prerequisite, setPrerequisite] = useState('');
  const [prerequisitesList, setPrerequisitesList] = useState([]);

  // Fetch projects associated with the teacher's userId
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/projects/teacher/${window.location.pathname.split('/')[2]}`);
        setProjects(response.data);
      } catch (error) {
        console.error('Error fetching projects:', error);
      }
    };

    fetchProjects();
  }, []);

  const handleAddPrerequisite = () => {
    if (prerequisite.trim() !== '') {
      setPrerequisitesList([...prerequisitesList, prerequisite]);
      setPrerequisite('');
    }
  };

  const handleDeletePrerequisite = (index) => {
    const updatedPrerequisites = prerequisitesList.filter((_, i) => i !== index);
    setPrerequisitesList(updatedPrerequisites);
  };

  const handleSaveProject = async () => {
    try {
      if (isEditing) {
        // Update existing project
        await axios.put(`http://localhost:8000/projects/${editedProject._id}`, editedProject);
      } else {
        // Add new project
        await axios.post('http://localhost:8000/projects', {
          teacher_id: window.location.pathname.split('/')[2],
          ...editedProject
        });
      }
      setIsEditing(false);
      setEditedProject({
        _id: '',
        name: '',
        description: '',
        cg_cutoff: '',
        prerequisites: []
      });
      setShowForm(false); // Hide the form after saving
      fetchProjects(); // Fetch updated list of projects
    } catch (error) {
      console.error('Error saving project:', error);
    }
  };


  const handleEdit = (project) => {
    setIsEditing(true); // Enable form for editing
    setEditedProjectId(project._id); // Set edited project ID
    setProjectName(project.name);
    setProjectDescription(project.description);
    setCgCutoff(project.cg_cutoff);
    setPrerequisitesList(project.prerequisites);
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Teacher Home</h1>
      <button onClick={() => setShowForm(!showForm)}>Add Project</button>
      {showForm && (
        <div>
          <div style={{ border: '1px solid black', padding: '20px', margin: '20px' }}>
            <form>
              <label>Name of the Project:</label><br />
              <input
                type="text"
                value={projectName}
                onChange={(e) => setProjectName(e.target.value)}
              /><br />
              <label>Project Description:</label><br />
              <textarea
                value={projectDescription}
                onChange={(e) => setProjectDescription(e.target.value)}
              /><br />
              <label>CG Cutoff:</label><br />
              <select
                value={cgCutoff}
                onChange={(e) => setCgCutoff(e.target.value)}
              >
                <option value="">Select CG Cutoff</option>
                <option value="9 or above">9 or above</option>
                <option value="8 or above">8 or above</option>
                <option value="7 or above">7 or above</option>
                <option value="6 or above">6 or above</option>
                <option value="open for all">Open for all</option>
              </select><br />
              <label>Prerequisites:</label><br />
              <input
                type="text"
                value={prerequisite}
                onChange={(e) => setPrerequisite(e.target.value)}
              />
              <button type="button" onClick={handleAddPrerequisite}>Add New +</button><br />
              {prerequisitesList.map((item, index) => (
                <div key={index}>
                  <span>{item}</span>
                  <button type="button" onClick={() => handleDeletePrerequisite(index)}>X</button>
                </div>
              ))}
              <button onClick={handleSaveProject}>{isEditing ? 'Save' : 'Add'}</button>
            </form>
          </div>
        </div>
      )}

      <div>
        <h2>Projects</h2>
        {projects.map((project, index) => (
          <div key={index}>
            <h3>{project.name}</h3>
            <p>Description: {project.description}</p>
            <p>CG Cutoff: {project.cg_cutoff}</p>
            <p>Prerequisites: {project.prerequisites.join(', ')}</p>
            <button onClick={() => handleEdit(project)}>Edit</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeacherHome;
