import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TeacherHome = ({ userId }) => {
  const [projects, setProjects] = useState([]);
  const [newProject, setNewProject] = useState({
    project_name: '',
    project_type: '',
    project_description: '',
    project_domain: '',
    cg_cutoff: '',
    pre_requisites: [],
  });

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/projects/${userId}`);
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const handleAddProject = async () => {
    try {
      const response = await axios.post(`http://localhost:8000/projects/${userId}`, newProject);
      setProjects([...projects, response.data]);
      setNewProject({
        project_name: '',
        project_type: '',
        project_description: '',
        project_domain: '',
        cg_cutoff: '',
        pre_requisites: [],
      });
    } catch (error) {
      console.error('Error adding project:', error);
    }
  };

  const handleEditProject = async (projectId) => {
    // Implement the logic to fetch the project details for editing
    // Update the newProject state with the fetched details
  };

  const handleUpdateProject = async () => {
    // Implement the logic to update the project details in the database
  };

  const handleDeleteProject = async (projectId) => {
    // Implement the logic to delete the project from the database
  };

  useEffect(() => {
    fetchProjects();
  }, [userId]);

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Teacher Home</h1>
      <button onClick={handleAddProject}>ADD PROJECT</button>
      <ul>
        {projects.map((project) => (
          <li key={project._id}>
            {project.project_name} - {project.project_description}
            <button onClick={() => handleEditProject(project._id)}>EDIT</button>
            <button onClick={() => handleDeleteProject(project._id)}>DELETE</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TeacherHome;
