import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const TeacherProfile = () => {
  const { userId } = useParams();
  const [teacherData, setTeacherData] = useState(null);
  const [formData, setFormData] = useState({
    name: "",
    roomNumber: "",
    department: ""
  });

  useEffect(() => {
    fetchTeacherData(userId);
  }, [userId]);

  const fetchTeacherData = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:8000/teachers/${userId}`);
      setTeacherData(response.data);
    } catch (error) {
      console.error("Error fetching teacher data:", error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:8000/teachers/${userId}`, formData);
      // After successful submission, fetch updated data again
      fetchTeacherData(userId);
    } catch (error) {
      console.error("Error saving teacher data:", error);
    }
  };

  if (!teacherData) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Teacher Profile</h1>
      <form onSubmit={handleSubmit}>
        <h2>My Profile</h2>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" name="name" value={formData.name} onChange={handleInputChange} />
        <label htmlFor="roomNumber">Room Number:</label>
        <input type="text" id="roomNumber" name="roomNumber" value={formData.roomNumber} onChange={handleInputChange} />
        <label htmlFor="department">Department:</label>
        <input type="text" id="department" name="department" value={formData.department} onChange={handleInputChange} />
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default TeacherProfile;
