import React, { useState, useEffect } from "react";
import EditModal from "./EditModal"; // Import the EditModal component

function TeacherHome() {
  const [projects, setProjects] = useState([]);
  const [editModalOpen, setEditModalOpen] = useState(false);
  const [selectedProjectId, setSelectedProjectId] = useState(null);

  useEffect(() => {
    // Fetch projects data
    const fetchProjects = async () => {
      try {
        const response = await fetch("http://localhost:8000/teacherprojects");
        if (!response.ok) {
          throw new Error("Failed to fetch projects");
        }
        const data = await response.json();
        setProjects(data);
      } catch (error) {
        console.error("Error fetching projects:", error);
      }
    };

    fetchProjects();
  }, []);

  const handleEditProject = (projectId) => {
    setSelectedProjectId(projectId);
    setEditModalOpen(true);
  };

  const handleCloseEditModal = () => {
    setEditModalOpen(false);
    setSelectedProjectId(null);
  };

  return (
    <div>
      {/* Render projects */}
      {projects.map((project) => (
        <div key={project._id}>
          <h2>{project.project_name}</h2>
          <p>{project.project_description}</p>
          {/* Add edit button */}
          <button onClick={() => handleEditProject(project._id)}>Edit</button>
        </div>
      ))}
      
      {/* Render EditModal if editModalOpen is true */}
      {editModalOpen && (
        <EditModal
          projectId={selectedProjectId}
          closeModal={handleCloseEditModal}
        />
      )}
    </div>
  );
}

export default TeacherHome;
