import React, { useState, useEffect } from 'react';
import { classNames } from 'primereact/utils';
import { FilterMatchMode } from 'primereact/api';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { Dropdown } from 'primereact/dropdown';
import { TriStateCheckbox } from 'primereact/tristatecheckbox';

export default function ProjectBank({ match }) {
    const userId = match.params.userId;
    const [projects, setProjects] = useState(null);
    const [loading, setLoading] = useState(true);
    const [globalFilterValue, setGlobalFilterValue] = useState('');

    const filters = {
        global: { value: null, matchMode: FilterMatchMode.CONTAINS },
        project_name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
        project_domain: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
        teacher_name: { value: null, matchMode: FilterMatchMode.STARTS_WITH },
        project_type: { value: null, matchMode: FilterMatchMode.EQUALS },
        department: { value: null, matchMode: FilterMatchMode.EQUALS },
        cg_eligibility: { value: null, matchMode: FilterMatchMode.EQUALS }
    };

    useEffect(() => {
        fetchProjects();
    }, []); // eslint-disable-line react-hooks/exhaustive-deps

    const fetchProjects = async () => {
        try {
            const response = await fetch(`http://localhost:8000/students/projectBank/${userId}`);
            if (!response.ok) {
                throw new Error('Failed to fetch projects');
            }
            const data = await response.json();
            setProjects(data);
            setLoading(false);
        } catch (error) {
            console.error(error);
        }
    };

    const onGlobalFilterChange = (e) => {
        const value = e.target.value;
        setGlobalFilterValue(value);
    };

    const header = (
        <div className="flex justify-content-end">
            <span className="p-input-icon-left">
                <i className="pi pi-search" />
                <InputText value={globalFilterValue} onChange={onGlobalFilterChange} placeholder="Keyword Search" />
            </span>
        </div>
    );

    const cgEligibilityBodyTemplate = (rowData) => {
        return <TriStateCheckbox value={rowData.cg_eligibility} />;
    };

    return (
        <div className="card">
            <DataTable value={projects} paginator rows={10} dataKey="id" filters={filters} filterDisplay="row" loading={loading}
                globalFilterFields={['project_name', 'project_domain', 'teacher_name']} header={header} emptyMessage="No projects found.">
                <Column field="project_name" header="Project Name" filter filterPlaceholder="Search by name" />
                <Column field="project_description" header="Project Description" />
                <Column field="project_type" header="Project Type" filter filterPlaceholder="Select" />
                <Column field="project_domain" header="Project Domain" filter filterPlaceholder="Search by domain" />
                <Column field="teacher_name" header="Teacher Name" filter filterPlaceholder="Search by name" />
                <Column field="department" header="Department" filter filterPlaceholder="Select" />
                <Column field="pre_requisites" header="Pre-requisites" />
                <Column field="cg_eligibility" header="CG Eligibility" body={cgEligibilityBodyTemplate} />
            </DataTable>
        </div>
    );
}
