import React, { useEffect, useState } from 'react'
// import "./Header.css"

import axios from "axios"
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import Offcanvas from 'react-bootstrap/Offcanvas';

const Headers = () => {
    const [userdata, setUserdata] = useState({});
    console.log("response", userdata)

    const getUser = async () => {
        try {
            const response = await axios.get("http://localhost:8000/login/success", { withCredentials: true });

            setUserdata(response.data.user)
        } catch (error) {
            console.log("error", error)
        }
    }

    // logoout
    const logout = ()=>{
        window.open("http://localhost:8000/logout","_self")
    }

    useEffect(() => {
        getUser()
    }, [])
    return (
        <>
            <Navbar className="bg-body-tertiary">
                <Container>
                <Navbar.Brand href="#home"> Project Bridge </Navbar.Brand>
                    <Navbar.Toggle />
                    <Navbar.Collapse className="justify-content-end">
                    <Navbar.Text>
                        Signed in as: <a href="#login"> {userdata?.displayName}</a>
                    </Navbar.Text>
                    {
                                Object?.keys(userdata)?.length > 0 ? (
                                    <>
                                        <Navbar.Text>
                                            Signed in as: <a href="#login"> {userdata?.displayName}</a>
                                        </Navbar.Text>
                                        <li>
                                            <NavLink to="/dashboard">
                                                Dashboard
                                            </NavLink>
                                        </li>
                                        <li onClick={logout}>Logout</li>
                                        <li>
                                            <img src={userdata?.image} style={{ width: "50px", borderRadius: "50%" }} alt="" />
                                        </li>
                                    </>
                                ) : <li>
                                    <NavLink to="/login">
                                        Login
                                    </NavLink>
                                </li>
                            }
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </>
    )
}

export default Headers
