import React, { useState } from 'react';
import './teacher_home.css'

const TeacherHome = () => {
  const [showModal, setShowModal] = useState(false);
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [projectType, setProjectType] = useState('LOP'); // Default project type
  const [projectDomain, setProjectDomain] = useState('CS'); // Default project domain
  const [preRequisites, setPreRequisites] = useState([]);

  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  const handleProjectNameChange = (event) => setProjectName(event.target.value);
  const handleProjectDescriptionChange = (event) => setProjectDescription(event.target.value);
  const handleProjectTypeChange = (event) => setProjectType(event.target.value);
  const handleProjectDomainChange = (event) => setProjectDomain(event.target.value);

  const handleAddPreRequisite = () => {
    if (projectName !== '') {
      setPreRequisites([...preRequisites, projectName]);
      setProjectName(''); // Clear input field after adding
    }
  };

  const handleRemovePreRequisite = (index) => {
    const newPreRequisites = [...preRequisites];
    newPreRequisites.splice(index, 1);
    setPreRequisites(newPreRequisites);
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Teacher Home</h1>
      <button onClick={handleOpenModal}>ADD PROJECT</button>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h3>Add Project</h3>
            <form>
              <label htmlFor="projectName">Project Name:</label>
              <input
                type="text"
                id="projectName"
                value={projectName}
                onChange={handleProjectNameChange}
              />

              <label htmlFor="projectDescription">Project Description:</label>
              <textarea
                id="projectDescription"
                value={projectDescription}
                onChange={handleProjectDescriptionChange}
              />

              <label>Project Type:</label>
              <div className="radio-group">
                <input
                  type="radio"
                  id="lop"
                  name="projectType"
                  value="LOP"
                  checked={projectType === 'LOP'}
                  onChange={handleProjectTypeChange}
                />
                <label htmlFor="lop">LOP</label>

                <input
                  type="radio"
                  id="dop"
                  name="projectType"
                  value="DOP"
                  checked={projectType === 'DOP'}
                  onChange={handleProjectTypeChange}
                />
                <label htmlFor="dop">DOP</label>

                <input
                  type="radio"
                  id="sop"
                  name="projectType"
                  value="SOP"
                  checked={projectType === 'SOP'}
                  onChange={handleProjectTypeChange}
                />
                <label htmlFor="sop">SOP</label>
              </div>

              <label htmlFor="projectDomain">Project Domain:</label>
              <select id="projectDomain" value={projectDomain} onChange={handleProjectDomainChange}>
                <option value="CS">CS</option>
                <option value="ECE">ECE</option>
                <option value="ENI">ENI</option>
                <option value="EEE">EEE</option>
              </select>

              <label htmlFor="preRequisites">Pre-requisites:</label>
              <div className="pre-requisites">
                <input
                  type="text"
                  id="preRequisites"
                  value={projectName} // Use current project name as default
                  onChange={handleProjectNameChange}
                />
                <button onClick={handleAddPreRequisite}>Add New +</button>
              </div>

              {preRequisites.length > 0 && (
                <ul className="pre-requisites-list">
                  {preRequisites.map((preRequisite, index) => (
                    <li key={index}>
                      {preRequisite}
                      <span onClick={() => handleRemovePreRequisite(index)}>x</span>
                    </li>
                
                  ))}
                </ul>
              )}

              <button type="submit">Submit</button>
            </form>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherHome;
