// TeacherHome.js
import React, { useState, useEffect } from "react";
import ProjectForm from "./form";
import EditModal from "./EditModal"; // Import the EditModal component
import { useParams } from "react-router-dom";
import {
  CButton,
  CCollapse,
  CCard,
  CCardBody,
  CCardTitle,
  CCardText
} from '@coreui/react';

function TeacherHome() {
  const [visible, setVisible] = useState(false);
  const [projects, setProjects] = useState([]);
  const [editModalVisible, setEditModalVisible] = useState(false);
  const [selectedProjectId, setSelectedProjectId] = useState(null);
  const { userId } = useParams();

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await fetch(`http://localhost:8000/projects/${userId}`);
      const data = await response.json();
      setProjects(data);
    } catch (error) {
      console.error("Error fetching projects:", error);
    }
  };

  const saveProject = async (projectData) => {
    try {
      const response = await fetch(`http://localhost:8000/saveProject`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(projectData),
      });
      const data = await response.json();
      console.log("Project saved successfully:", data);
      fetchProjects(); // Fetch projects again after saving
    } catch (error) {
      console.error("Error saving project:", error);
    }
  };
  

  const toggleExpandCollapse = (index) => {
    const newProjects = [...projects];
    newProjects[index].expanded = !newProjects[index].expanded;
    setProjects(newProjects);
  };

  const openEditModal = (projectId) => {
    setSelectedProjectId(projectId);
    setEditModalVisible(true);
  };

  const closeEditModal = () => {
    setEditModalVisible(false);
    setSelectedProjectId(null);
  };

  return (
    <>
      <CButton className="mb-3" onClick={() => setVisible(!visible)} aria-expanded={visible} aria-controls="collapseWidthExample">Add Project</CButton>
      <div>
        <CCollapse id="collapseWidthExample" visible={visible}>
          <CCard style={{"width":"70%", left:"14%"}}>
            <CCardBody>
              <ProjectForm
                saveProject={saveProject}
                closeModal={() => setVisible(false)}
                teacherId={userId}
              />
            </CCardBody>
          </CCard>
        </CCollapse>
      </div>
      <hr></hr>
      <div>
        <h3>Current Projects</h3>
        {projects.map((project, index) => (
          <CCard key={project._id} style={{ width: '18rem' }}>
            <CCardBody>
              <CCardTitle>{project.project_name}</CCardTitle>
              <CCardText>{project.project_description}</CCardText>
              {project.expanded && <CCardText>CG Cutoff: {project.cg_cutoff}</CCardText>}
              <CButton id="expand_collapse" onClick={() => toggleExpandCollapse(index)}>
                {project.expanded ? "Collapse" : "Expand"}
              </CButton>
              <CButton onClick={() => openEditModal(project._id)}>Edit</CButton>
              <CButton>Delete</CButton>
            </CCardBody>
          </CCard>
        ))}
      </div>
      {/* Edit Modal */}
      {editModalVisible && (
  <EditModal
    projectId={selectedProjectId} // Pass projectId to the EditModal
    closeModal={closeEditModal}
  />
)}


    </>
  );
}

export default TeacherHome;
