import React, { useState, useEffect } from 'react';
import ProjectForm from './projectForm';

const TeacherHome = () => {
  const [showModal, setShowModal] = useState(false);

  // Load showModal state from localStorage on component mount
  useEffect(() => {
    const savedShowModal = localStorage.getItem('showModal');
    if (savedShowModal) {
      setShowModal(JSON.parse(savedShowModal));
    }
  }, []);

  // Save showModal state to localStorage when it changes
  useEffect(() => {
    localStorage.setItem('showModal', JSON.stringify(showModal));
  }, [showModal]);

  const handleAddProject = async (project) => {
    // Your handleAddProject function remains unchanged
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Teacher Home</h1>
      <button onClick={() => setShowModal(true)}>Add Project</button>
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setShowModal(false)}>
              &times;
            </span>
            <ProjectForm onSubmit={handleAddProject} />
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherHome;
