import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
const TeacherHome = ({ match }) => {
  const { userId } = useParams();
  const teacherId = userId;
  const [projects, setProjects] = useState([]);
  const [projectFormData, setProjectFormData] = useState({
    project_name: '',
    project_type: '',
    project_description: '',
    project_domain: '',
    cg_cutoff: '',
    pre_requisites: [],
  });

  useEffect(() => {
    // Fetch and set the projects for the current teacher
    axios.get(`http://localhost:8000/projects/${teacherId}`)
      .then((response) => setProjects(response.data))
      .catch((error) => console.error('Error fetching projects:', error));
  }, [teacherId]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProjectFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleAddProject = () => {
    // Add project to the database
    axios.post('http://localhost:8000/projects', { teacherId, ...projectFormData })
      .then(() => {
        // Refresh the projects list after adding
        axios.get(`http://localhost:8000/projects/${teacherId}`)
          .then((response) => setProjects(response.data))
          .catch((error) => console.error('Error fetching projects:', error));
      })
      .catch((error) => console.error('Error adding project:', error));
  };

  const handleEditProject = (projectId) => {
    // Fetch the project details for editing
    axios.get(`http://localhost:8000/projects/details/${projectId}`)
      .then((response) => setProjectFormData(response.data))
      .catch((error) => console.error('Error fetching project details:', error));
  };

  const handleUpdateProject = () => {
    // Update project in the database
    axios.put(`http://localhost:8000/projects/${projectFormData._id}`, projectFormData)
      .then(() => {
        // Refresh the projects list after updating
        axios.get(`http://localhost:8000/projects/${teacherId}`)
          .then((response) => setProjects(response.data))
          .catch((error) => console.error('Error fetching projects:', error));
      })
      .catch((error) => console.error('Error updating project:', error));
  };

  const handleDeleteProject = (projectId) => {
    // Delete project from the database
    axios.delete(`http://localhost:8000/projects/${projectId}`)
      .then(() => {
        // Refresh the projects list after deleting
        axios.get(`http://localhost:8000/projects/${teacherId}`)
          .then((response) => setProjects(response.data))
          .catch((error) => console.error('Error fetching projects:', error));
      })
      .catch((error) => console.error('Error deleting project:', error));
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Teacher Home</h1>
      <button onClick={() => handleAddProject()}>ADD PROJECT</button>

      {/* Form for adding/editing project */}
      <form>
        {/* Include input fields for project details and bind them to handleInputChange */}
        <button onClick={() => handleUpdateProject()}>SAVE</button>
      </form>

      {/* Display projects */}
      <div>
        {projects.map((project) => (
          <div key={project._id}>
            <h3>{project.project_name}</h3>
            <p>{project.project_description}</p>
            <button onClick={() => handleEditProject(project._id)}>EDIT</button>
            <button onClick={() => handleDeleteProject(project._id)}>DELETE</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeacherHome;
