import React, { useState, useEffect } from 'react';
import ProjectDetailsForm from './project_details';
import axios from 'axios';

const TeacherHome = ({ match }) => {
    const [projects, setProjects] = useState([]);
    const [showProjectForm, setShowProjectForm] = useState(false);
    const [editingProject, setEditingProject] = useState(null);

    useEffect(() => {
        // Fetch projects for the teacher from the backend using userId
        const fetchProjects = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/projects/${match.params.userId}`);
                setProjects(response.data);
            } catch (error) {
                console.error('Error fetching projects:', error);
            }
        };

        fetchProjects();
    }, [match.params.userId]);

    const handleAddProject = () => {
        setShowProjectForm(true);
    };

    const handleEditProject = (project) => {
        setEditingProject(project);
        setShowProjectForm(true);
    };

    const handleDeleteProject = async (projectId) => {
        try {
            await axios.delete(`http://localhost:8000/projects/${projectId}`);
            setProjects(projects.filter(project => project._id !== projectId));
        } catch (error) {
            console.error('Error deleting project:', error);
        }
    };

    return (
        <div style={{ textAlign: "center" }}>
            <h1>Teacher Home</h1>
            <button onClick={handleAddProject}>ADD PROJECT</button>
            {showProjectForm && <ProjectDetailsForm
                editingProject={editingProject}
                setEditingProject={setEditingProject}
                setShowProjectForm={setShowProjectForm}
                setProjects={setProjects}
                userId={match.params.userId}
            />}
            <div>
                {projects.map(project => (
                    <div key={project._id}>
                        <h2>{project.project_name}</h2>
                        <p>{project.project_description}</p>
                        <p>{project.project_domain}</p>
                        <button onClick={() => handleEditProject(project)}>EDIT</button>
                        <button onClick={() => handleDeleteProject(project._id)}>DELETE</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TeacherHome;
