import React, { useState } from 'react';

const TeacherHome = () => {
  const [showForm, setShowForm] = useState(false);
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [cgCutoff, setCgCutoff] = useState('');
  const [preRequisites, setPreRequisites] = useState([]);

  const handleAddPreRequisite = (setPreRequisiteInput) => {
    if (preRequisites.includes(preRequisiteInput)) return;
    setPreRequisites([...preRequisites, preRequisiteInput]);
    setPreRequisiteInput('');
  };

  const handleDeletePreRequisite = (index) => {
    const newPreRequisites = [...preRequisites];
    newPreRequisites.splice(index, 1);
    setPreRequisites(newPreRequisites);
  };

  const [preRequisiteInput, setPreRequisiteInput] = useState('');

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Teacher Home</h1>
      <button onClick={() => setShowForm(!showForm)}>Add Project</button>
      {showForm && (
        <form>
          <label>
            Name of the project:
            <input
              type="text"
              value={projectName}
              onChange={(e) => setProjectName(e.target.value)}
            />
          </label>
          <br />
          <label>
            Project description:
            <textarea
              value={projectDescription}
              onChange={(e) => setProjectDescription(e.target.value)}
            />
          </label>
          <br />
          <label>
            CG Cutoff:
            <select value={cgCutoff} onChange={(e) => setCgCutoff(e.target.value)}>
              <option value="9">9 or above</option>
              <option value="8">8 or above</option>
              <option value="7">7 or above</option>
              <option value="6">6 or above</option>
              <option value="open">Open for all</option>
            </select>
          </label>
          <br />
          <label>
            Pre-requisites:
            <input
              type="text"
              value={preRequisiteInput}
              onChange={(e) => setPreRequisiteInput(e.target.value)}
            />
            <button onClick={() => handleAddPreRequisite(setPreRequisiteInput)}>
              Add
            </button>
          </label>
          <ul>
            {preRequisites.map((preRequisite, index) => (
              <li key={index}>
                {preRequisite}
                <button onClick={() => handleDeletePreRequisite(index)}>
                  X
                </button>
              </li>
            ))}
          </ul>
        </form>
      )}
    </div>
  );
};

export default TeacherHome;
