import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TeacherHome = ({ userId }) => {
  const [projects, setProjects] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedProject, setSelectedProject] = useState({
    project_name: '',
    project_description: '',
    project_domain: '',
  });

  // Fetch projects on component mount
  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/projects/${userId}`);
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  const openModal = () => {
    setShowModal(true);
    setSelectedProject({
      project_name: '',
      project_description: '',
      project_domain: '',
    });
  };

  const closeModal = () => {
    setShowModal(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSelectedProject({ ...selectedProject, [name]: value });
  };

  const addOrUpdateProject = async () => {
    try {
      if (selectedProject._id) {
        await axios.put(`http://localhost:8000/projects/${selectedProject._id}`, selectedProject);
      } else {
        await axios.post('http://localhost:8000/projects', { ...selectedProject, teacherId: userId });
      }
      closeModal();
      fetchProjects();
    } catch (error) {
      console.error('Error adding/updating project:', error);
    }
  };

  const editProject = (project) => {
    setSelectedProject(project);
    setShowModal(true);
  };

  const deleteProject = async (projectId) => {
    try {
      await axios.delete(`http://localhost:8000/projects/${projectId}`);
      fetchProjects();
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Teacher Home</h1>
      <button onClick={openModal}>Add Project</button>
      {projects.map((project) => (
        <div key={project._id}>
          <h2>{project.project_name}</h2>
          <p>{project.project_description}</p>
          <p>{project.project_domain}</p>
          <button onClick={() => editProject(project)}>Edit</button>
          <button onClick={() => deleteProject(project._id)}>Delete</button>
        </div>
      ))}
      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>
              &times;
            </span>
            <h2>Add/Edit Project</h2>
            <input
              type="text"
              name="project_name"
              placeholder="Project Name"
              value={selectedProject.project_name}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="project_description"
              placeholder="Project Description"
              value={selectedProject.project_description}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="project_domain"
              placeholder="Project Domain"
              value={selectedProject.project_domain}
              onChange={handleInputChange}
            />
            <button onClick={addOrUpdateProject}>Save</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TeacherHome;
