import React, { useState } from 'react';

const ProjectDetailsForm = ({ project, onEdit, onDelete }) => {
  const [isEditing, setIsEditing] = useState(false);

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleSaveClick = () => {
    setIsEditing(false);
    onEdit(project); // Pass the updated project object
  };

  const handleInputChange = (e, field) => {
    const updatedProject = { ...project, [field]: e.target.value };
    onEdit(updatedProject); // Pass the updated project object
  };

  return (
    <div>
      <label>Name of the Project:</label><br />
      <input
        type="text"
        value={project.name}
        disabled={!isEditing}
        onChange={(e) => handleInputChange(e, 'name')} // Pass the field name
      /><br />
      <label>Project Description:</label><br />
      <textarea
        value={project.description}
        disabled={!isEditing}
        onChange={(e) => handleInputChange(e, 'description')} // Pass the field name
      /><br />
      <label>CG Cutoff:</label><br />
      <select
        value={project.cg_cutoff}
        disabled={!isEditing}
        onChange={(e) => handleInputChange(e, 'cg_cutoff')} // Pass the field name
      >
        <option value="">Select CG Cutoff</option>
        <option value="9 or above">9 or above</option>
        <option value="8 or above">8 or above</option>
        <option value="7 or above">7 or above</option>
        <option value="6 or above">6 or above</option>
        <option value="open for all">Open for all</option>
      </select><br />
      <label>Prerequisites:</label><br />
      <input
        type="text"
        value={project.prerequisites.join(', ')}
        disabled={!isEditing}
        onChange={(e) => handleInputChange(e, 'prerequisites')} // Pass the field name
      />
      {isEditing && <button onClick={handleSaveClick}>Save</button>}
      {!isEditing && <button onClick={handleEditClick}>Edit</button>}
    </div>
  );
};

export default ProjectDetailsForm;
