import React, { useState } from 'react';

const ProjectDetailsForm = ({ project, onEdit }) => {
  const [isEditing, setIsEditing] = useState(false);

  const handleEditClick = () => {
    setIsEditing(true);
    onEdit(); // Notify parent component that edit mode is activated
  };

  return (
    <div>
      <label>Name of the Project:</label><br />
      <input
        type="text"
        value={project.name}
        disabled={!isEditing} // Enable/disable based on editing mode
        onChange={(e) => onEdit({ ...project, name: e.target.value })} // Update parent component state
      /><br />
      <label>Project Description:</label><br />
      <textarea
        value={project.description}
        disabled={!isEditing}
        onChange={(e) => onEdit({ ...project, description: e.target.value })}
      /><br />
      <label>CG Cutoff:</label><br />
      <select
        value={project.cg_cutoff}
        disabled={!isEditing}
        onChange={(e) => onEdit({ ...project, cg_cutoff: e.target.value })}
      >
        <option value="">Select CG Cutoff</option>
        <option value="9 or above">9 or above</option>
        <option value="8 or above">8 or above</option>
        <option value="7 or above">7 or above</option>
        <option value="6 or above">6 or above</option>
        <option value="open for all">Open for all</option>
      </select><br />
      <label>Prerequisites:</label><br />
      <input
        type="text"
        value={project.prerequisites.join(', ')}
        disabled={!isEditing}
        onChange={(e) => onEdit({ ...project, prerequisites: e.target.value.split(', ') })}
      />
      {!isEditing && <button onClick={handleEditClick}>Edit</button>}
    </div>
  );
};

export default ProjectDetailsForm;
