// EditModal.js
import React, { useState, useEffect } from "react";
import {
  CButton,
  CModal,
  CModalHeader,
  CModalTitle,
  CModalBody,
  CModalFooter,
  CForm,
  CFormInput,
  CFormTextarea,
  CFormSelect,
  CInputGroup,
  CContainer,
  CRow,
  CCol,
  CBadge,
  CCloseButton
} from '@coreui/react';

function EditModal({ projectId, closeModal }) {
    const [projectData, setProjectData] = useState({
      projectName: "",
      projectDescription: "",
      projectType: "",
      cgpaCutoff: "",
      projectDomain: "",
      prerequisites: [],
      newPrerequisite: ""
    });
  
    useEffect(() => {
      fetchProjectData();
    }, []);
  
    const fetchProjectData = async () => {
      try {
        const response = await fetch(`http://localhost:8000/projects/${projectId}`);
        if (!response.ok) {
          throw new Error("Failed to fetch project data");
        }
        const data = await response.json();
        setProjectData({
          projectName: data.project_name || "",
          projectDescription: data.project_description || "",
          projectType: data.project_type || "",
          cgpaCutoff: data.cg_cutoff || "",
          projectDomain: data.project_domain || "",
          prerequisites: data.pre_requisites || [],
          newPrerequisite: ""
        });
      } catch (error) {
        console.error("Error fetching project data:", error);
      }
    };
  
    const handleSaveProject = async (e) => {
      e.preventDefault();
      try {
        const response = await fetch("http://localhost:8000/saveProject", {
          method: "PUT", // Use PUT method to update existing project
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            projectId,
            ...projectData
          }),
        });
        const data = await response.json();
        console.log("Project updated successfully:", data);
        closeModal(); // Close the modal after saving
      } catch (error) {
        console.error("Error saving project:", error);
      }
    };
  
    const handleAddPrerequisite = () => {
      setProjectData(prevState => ({
        ...prevState,
        prerequisites: [...prevState.prerequisites, prevState.newPrerequisite],
        newPrerequisite: ""
      }));
    };
  
    const handleDeletePrerequisite = (index) => {
      setProjectData(prevState => ({
        ...prevState,
        prerequisites: prevState.prerequisites.filter((_, i) => i !== index)
      }));
    };
  
    // Only render the form when project data is available
    if (!projectData.projectName) {
      return null;
    }
  
  return (
    <CModal
      backdrop="static"
      visible={true} // Always visible when opened
      onClose={closeModal}
      aria-labelledby="EditModalLabel"
    >
      <CModalHeader closeButton>
        <CModalTitle id="EditModalLabel">Edit Project</CModalTitle>
      </CModalHeader>
      <CModalBody>
        <CForm className="row g-3 needs-validation" noValidate onSubmit={handleSaveProject}>
          <CContainer>
            <CRow>
              <CCol>
                <CFormInput
                  type="text"
                  value={projectData.projectName}
                  floatingLabel="Project Name"
                  onChange={(e) => setProjectData({...projectData, projectName: e.target.value})}
                  required
                />
              </CCol>
              <CCol xs={6}>
                <CFormTextarea
                  floatingLabel="Description"
                  value={projectData.projectDescription}
                  placeholder="Enter Project Description here"
                  style={{ minHeight: '7rem' }}
                  onChange={(e) => setProjectData({...projectData, projectDescription: e.target.value})}
                  required
                />
              </CCol>
            </CRow>
            <br></br>
            <CRow>
              <CCol>
                <CFormSelect
                  floatingLabel="Project Type"
                  value={projectData.projectType}
                  onChange={(e) => setProjectData({...projectData, projectType: e.target.value})}
                  options={[
                    'Select',
                    { label: 'Design Project (DOP)', value: 'DOP' },
                    { label: 'Label Project (LOP)', value: 'LOP' },
                    { label: 'Study Project (SOP)', value: 'SOP' },
                  ]}
                />
              </CCol>
              <CCol>
                <CFormSelect
                  floatingLabel="CGPA cutoff"
                  value={projectData.cgpaCutoff}
                  onChange={(e) => setProjectData({...projectData, cgpaCutoff: e.target.value})}
                  options={[
                    'Select',
                    { label: '6.0 or More', value: '6.0' },
                    { label: '7.0 or More', value: '7.0' },
                    { label: '8.0 or More', value: '8.0' },
                    { label: '9.0 or More', value: '9.0' }
                  ]}
                />
              </CCol>
            </CRow>
            <br></br>
            <CFormInput
              type="text"
              floatingLabel="Specify Domain"
              value={projectData.projectDomain}
              onChange={(e) => setProjectData({...projectData, projectDomain: e.target.value})}
            />
            <br></br>
            <CInputGroup className="mb-3">
              <CFormInput
                type="text"
                value={projectData.newPrerequisite}
                onChange={(e) => setProjectData({...projectData, newPrerequisite: e.target.value})}
                floatingLabel="Pre-requisites (if any)"
                aria-describedby="button-addon2"
              />
              <CButton type="button" color="secondary" variant="outline" id="button-addon2" onClick={handleAddPrerequisite}>
                Add New
              </CButton>
            </CInputGroup>
            <ul>
              {projectData.prerequisites.map((item, index) => (
                <CBadge key={index} shape="rounded-pill" style={{marginRight: '5px'}}>
                  {item}
                  <CCloseButton type="button" color="light" style={{width: '0.4em', height: '0.4em'}} onClick={() => handleDeletePrerequisite(index)}/>
                </CBadge>
              ))}
            </ul>
          </CContainer>
        </CForm>
      </CModalBody>
      <CModalFooter>
        <CButton color="secondary" onClick={closeModal}>
          Close
        </CButton>
        <CButton color="primary" type="submit">
          Save changes
        </CButton>
      </CModalFooter>
    </CModal>
  );
}

export default EditModal;
