import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TeacherHome = () => {
  const [projects, setProjects] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editedProject, setEditedProject] = useState({
    _id: '',
    name: '',
    description: '',
    cg_cutoff: '',
    prerequisites: []
  });

  const fetchProjects = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/projects/teacher/${window.location.pathname.split('/')[2]}`);
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  const handleEdit = (projectIndex) => {
    console.log("Editing project:", projectIndex);
    setIsEditing(true);
    const projectToEdit = projects[projectIndex];
    setEditedProject({
      _id: projectToEdit._id,
      name: projectToEdit.name,
      description: projectToEdit.description,
      cg_cutoff: projectToEdit.cg_cutoff,
      prerequisites: [...projectToEdit.prerequisites]
    });
  };
  
  
  
  const handleSaveProject = async () => {
    try {
      if (isEditing) {
        // Update existing project
        await axios.put(`http://localhost:8000/projects/${editedProject._id}`, editedProject);
      } else {
        // Add new project
        await axios.post('http://localhost:8000/projects', {
          teacher_id: window.location.pathname.split('/')[2],
          ...editedProject
        });
      }
      setIsEditing(false);
      setEditedProject({
        _id: '',
        name: '',
        description: '',
        cg_cutoff: '',
        prerequisites: []
      });
      setShowForm(false); // Hide the form after saving
      fetchProjects(); // Fetch updated list of projects
    } catch (error) {
      console.error('Error saving project:', error);
    }
  };

  const handleDelete = async (projectId) => {
    try {
      await axios.delete(`http://localhost:8000/projects/${projectId}`);
      // Filter out the deleted project from the projects state
      const updatedProjects = projects.filter(project => project._id !== projectId);
      setProjects(updatedProjects);
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };
  

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Teacher Home</h1>
      <button onClick={() => setShowForm(!showForm)}>Add Project</button>
      {showForm && (
        <div>
          <div style={{ border: '1px solid black', padding: '20px', margin: '20px' }}>
            <form>
              <label>Name of the Project:</label><br />
              <input
                type="text"
                value={editedProject.name}
                onChange={(e) => setEditedProject({ ...editedProject, name: e.target.value })}
              /><br />
              <label>Project Description:</label><br />
              <textarea
                value={editedProject.description}
                onChange={(e) => setEditedProject({ ...editedProject, description: e.target.value })}
              /><br />
              <label>CG Cutoff:</label><br />
              <select
                value={editedProject.cg_cutoff}
                onChange={(e) => setEditedProject({ ...editedProject, cg_cutoff: e.target.value })}
              >
                <option value="">Select CG Cutoff</option>
                <option value="9 or above">9 or above</option>
                <option value="8 or above">8 or above</option>
                <option value="7 or above">7 or above</option>
                <option value="6 or above">6 or above</option>
                <option value="open for all">Open for all</option>
              </select><br />
              <label>Prerequisites:</label><br />
              <input
                type="text"
                value={editedProject.prerequisites.join(', ')}
                onChange={(e) => setEditedProject({ ...editedProject, prerequisites: e.target.value.split(', ') })}
              />
              <button onClick={handleSaveProject}>{isEditing ? 'Save' : 'Add'}</button>
            </form>
          </div>
        </div>
      )}

      <div>
        <h2>Projects</h2>
        {projects.map((project, index) => (
          <div key={project._id} style={{ border: '1px solid black', padding: '10px', margin: '10px' }}>
            <h3>{project.name}</h3>
            <p>Description: {project.description}</p>
            <p>CG Cutoff: {project.cg_cutoff}</p>
            <p>Prerequisites: {project.prerequisites.join(', ')}</p>
            <button onClick={() => handleEdit(index)}>Edit</button>
            <button onClick={() => handleDelete(project._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeacherHome;
