import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ProjectDetailsForm from './project_details'; 

const TeacherHome = () => {
  const [projects, setProjects] = useState([]);
  const [selectedProject, setSelectedProject] = useState(null);

  // Fetch projects from the server
  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/projects/teacher/${window.location.pathname.split('/')[2]}`);
        setProjects(response.data);
      } catch (error) {
        console.error('Error fetching projects:', error);
      }
    };

    fetchProjects();
  }, []);

  // Function to handle editing project details
  const handleEditProject = (editedProject) => {
    // Find the index of the edited project in the projects array
    const index = projects.findIndex((project) => project._id === editedProject._id);
    // Create a copy of the projects array
    const updatedProjects = [...projects];
    // Replace the edited project with the updated project in the copied array
    updatedProjects[index] = editedProject;
    // Update the state with the new array of projects
    setProjects(updatedProjects);
  };

  // Function to handle deleting a project
  const handleDeleteProject = async (projectId) => {
    try {
      await axios.delete(`http://localhost:8000/projects/${projectId}`);
      // Filter out the deleted project from the projects array
      const updatedProjects = projects.filter((project) => project._id !== projectId);
      setProjects(updatedProjects);
    } catch (error) {
      console.error('Error deleting project:', error);
    }
  };

  // Function to handle adding a new project
  const handleAddProject = async () => {
    try {
      const response = await axios.post('http://localhost:8000/projects', {
        teacher_id: window.location.pathname.split('/')[2], // Assuming this is the teacher ID
        name: 'New Project',
        description: '',
        cg_cutoff: '',
        prerequisites: []
      });
      const newProject = response.data;
      setProjects([...projects, newProject]);
    } catch (error) {
      console.error('Error adding project:', error);
    }
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Teacher Home</h1>
      <div>
        <h2>Projects</h2>
        {projects.map((project) => (
          <div key={project._id} style={{ border: '1px solid black', padding: '10px', margin: '10px' }}>
            <h3>{project.name}</h3>
            <ProjectDetailsForm project={project} onEdit={handleEditProject} />
            <button onClick={() => handleDeleteProject(project._id)}>Delete</button>
          </div>
        ))}
        <button onClick={handleAddProject}>Add Project</button>
      </div>
    </div>
  );
};

export default TeacherHome;
