import React, { useState, useEffect } from "react";
import ProjectForm from "./form";
import { useParams } from "react-router-dom";
import {
  CButton,
  CCollapse,
  CCard,
  CCardBody,
  CCardHeader,
  CCardText,
  CRow,
  CCol
} from '@coreui/react';

function TeacherHome() {
  const [visible, setVisible] = useState(false);
  const [projects, setProjects] = useState([]);
  const { userId } = useParams();

  useEffect(() => {
    fetchProjects();
  }, []);

  const fetchProjects = async () => {
    try {
      const response = await fetch(`http://localhost:8000/projects/${userId}`);
      const data = await response.json();
      setProjects(data);
    } catch (error) {
      console.error("Error fetching projects:", error);
    }
  };

  const saveProject = async (projectData) => {
    projectData.teacherId = userId;

    try {
      const response = await fetch("http://localhost:8000/saveProject", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(projectData),
      });
      const data = await response.json();
      console.log("Project saved successfully:", data);
      fetchProjects(); // Fetch projects again after saving
    } catch (error) {
      console.error("Error saving project:", error);
    }
  };

  const card_styling = {
    'width':'50%',
    'margin':'2%'
  }
  const toggleExpandCollapse = (index) => {
    const newProjects = [...projects];
    newProjects[index].expanded = !newProjects[index].expanded;
    setProjects(newProjects);
  };

  return (
    <>
      <CButton className="mb-3" onClick={() => setVisible(!visible)} aria-expanded={visible} aria-controls="collapseWidthExample">Add Project</CButton>
      <div>
        <CCollapse id="collapseWidthExample" visible={visible}>
          <CCard style={{"width":"70%", left:"14%"}}>
            <CCardBody>
              <ProjectForm
                saveProject={saveProject}
                closeModal={() => setVisible(false)}
                teacherId={userId}
              />
            </CCardBody>
          </CCard>
        </CCollapse>
      </div>
      <hr></hr>
      <div>
        <h3>Current Projects</h3>
        {projects.map((project, index) => (
          <CCard key={project._id} style={card_styling}>
            <CCardHeader>{project.project_name}</CCardHeader>
            <CCardBody>
              <CCardText>{project.project_description}</CCardText>
              {project.expanded && 
              <>
              <CCardText>CG Cutoff: {project.cg_cutoff}</CCardText> 
              <CCardText>Project Domain: {project.project_domain}</CCardText>
              </>
              
              }
              <CRow>
                <CCol>
                  
                </CCol>
                <CCol>

                </CCol>
                <CCol>

                </CCol>
              </CRow>
              
              <CButton>Edit</CButton>
              <CButton>Delete</CButton>
            </CCardBody>
          </CCard>
        ))}
      </div>
    </>
  );
}

export default TeacherHome;
