import React, { useState } from 'react';
import {
  CModal,
  CModalHeader,
  CModalTitle,
  CModalBody,
  CModalFooter,
  CButton,
  CFormInput,
  CFormTextarea,
  CMultiSelect
} from '@coreui/react';

const RequestFormModal = ({ visible, onClose, project }) => {
  const [formData, setFormData] = useState({
    projectName: project.project_name || '',
    projectDescription: project.project_description || '',
    whyWantToDoProject: '',
    currentCGPA: '',
    selectedPrerequisites: []
  });

  const [showConfirmation, setShowConfirmation] = useState(false);
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handlePrerequisitesChange = (selectedOptions) => {
    const selectedPrerequisites = selectedOptions.map(option => option.label);
    setFormData({
      ...formData,
      selectedPrerequisites: selectedPrerequisites
    });
  };

  const handleSubmit = () => {
    // Show confirmation message
    setShowConfirmation(true);
  };

  const handleConfirmSendRequest = () => {
    // Handle sending request here
    // Once request is sent, close the modal
    setShowConfirmation(false);
    onClose();
  };

  const handleBack = () => {
    // Hide confirmation message
    setShowConfirmation(false);
  };

  const handleSaveDraft = () => {
    console.log("Save Draft");
  }

  // Dynamically generate options based on project prerequisites
  const options = project.pre_requisites.map((prerequisite, index) => {
    return {
      value: index,
      label: prerequisite
    };
  });

  return (
    <CModal
      backdrop="static"
      visible={visible}
      onClose={onClose}
      aria-labelledby="RequestFormModalTitle"
    >
      <CModalHeader closeButton>
        <CModalTitle id="RequestFormModalTitle">Request Form</CModalTitle>
      </CModalHeader>
      <CModalBody>
        {/* Form fields */}
        <CFormInput
          type="text"
          name="projectName"
          value={formData.projectName}
          onChange={handleInputChange}
          placeholder="Project Name"
          disabled
        />
        <CFormTextarea
          name="projectDescription"
          value={formData.projectDescription}
          onChange={handleInputChange}
          placeholder="Project Description"
          disabled
        />
        <CFormTextarea
          name="whyWantToDoProject"
          value={formData.whyWantToDoProject}
          onChange={handleInputChange}
          placeholder="Why do you want to do this project?"
        />
        <CFormInput
          type="number"
          name="currentCGPA"
          value={formData.currentCGPA}
          onChange={handleInputChange}
          placeholder="Current CGPA"
          step="0.01"
          min="0"
          max="10"
        />
        <CMultiSelect
          options={options}
          onChange={handlePrerequisitesChange}
          value={formData.selectedPrerequisites}
          label="Prerequisites"
          text="Please select your prerequisites."
        />
      </CModalBody>
      <CModalFooter>
        {!showConfirmation ? (
          <React.Fragment>
            <CButton color="secondary" onClick={onClose}>
              Close
            </CButton>
            <CButton color="primary" onClick={handleSaveDraft}>
              Save Draft
            </CButton>
            <CButton color="warning" onClick={handleSubmit}>
              Send Request
            </CButton>
          </React.Fragment>
        ) : (
          <React.Fragment>
            <CButton color="secondary" onClick={handleBack}>
              Back
            </CButton>
            <CButton color="success" onClick={handleConfirmSendRequest}>
              Confirm Send Request
            </CButton>
          </React.Fragment>
        )}
      </CModalFooter>
    </CModal>
  );
};

export default RequestFormModal;
