import React, { useEffect, useState } from 'react';
import axios from 'axios';
import "./Header.css"
import Dropdown from 'react-bootstrap/Dropdown';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import { useParams } from 'react-router-dom'; // Import useParams

const Headers = () => {
  const [userdata, setUserdata] = useState({});
  const [showDropdown, setShowDropdown] = useState(false);

  // Use useParams to get userId from the URL
  const { userId } = useParams();

  const getUser = async () => {
    try {
      // Fetch user data based on the userId
      const response = await axios.get(`http://localhost:8000/user/${userId}`, { withCredentials: true });
      setUserdata(response.data.user);
    } catch (error) {
      console.log("error", error);
    }
  }

  const logout = () => {
    window.open("http://localhost:8000/logout", "_self");
  }

  useEffect(() => {
    getUser();
  }, [userId]); // Trigger useEffect when userId changes

  const toggleDropdown = () => {
    setShowDropdown(!showDropdown);
  }

  return (
    <>
      <Navbar className="bg-body-tertiary mb-3">
        <Container fluid>
          <Navbar.Brand href="#">Hello {userdata?.displayName} ! </Navbar.Brand>
          <Dropdown show={showDropdown} onToggle={toggleDropdown}>
            <Dropdown.Toggle variant="light" id="dropdown-basic">
              <img src={userdata?.image} style={{ width: "50px", borderRadius: "50%" }} alt="" />
            </Dropdown.Toggle>
            <Dropdown.Menu>
              {userdata?.user_type === 'student' && (
                <Nav.Link href={`/StudentProfile/${userId}`}>My Profile</Nav.Link>
              )}
              {userdata?.user_type === 'teacher' && (
                <Nav.Link href={`/TeacherProfile/${userId}`}>My Profile</Nav.Link>
              )}
              <Dropdown.Item onClick={logout}>Logout</Dropdown.Item>
            </Dropdown.Menu>
          </Dropdown>
        </Container>
      </Navbar>
    </>
  );
}

export default Headers;
