// client/src/components/teacher/ProjectModal.js
import React, { useState } from 'react';

const ProjectModal = ({ show, onClose, onSave, project }) => {
  const [projectData, setProjectData] = useState({
    project_name: project ? project.project_name : '',
    project_description: project ? project.project_description : '',
    project_domain: project ? project.project_domain : '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProjectData({ ...projectData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave(project ? project._id : null, projectData);
  };

  return (
    show && (
      <div className="modal">
        <div className="modal-content">
          <span className="close" onClick={onClose}>
            &times;
          </span>
          <form onSubmit={handleSubmit}>
            <label>
              Project Name:
              <input type="text" name="project_name" value={projectData.project_name} onChange={handleChange} />
            </label>
            <label>
              Project Description:
              <input
                type="text"
                name="project_description"
                value={projectData.project_description}
                onChange={handleChange}
              />
            </label>
            <label>
              Project Domain:
              <input type="text" name="project_domain" value={projectData.project_domain} onChange={handleChange} />
            </label>
            <button type="submit">{project ? 'Save Changes' : 'Save'}</button>
          </form>
        </div>
      </div>
    )
  );
};

export default ProjectModal;
